import React, { Component } from 'react'

class TriviaItem extends Component {




    render() {
        const array = this.props.item.map((text,index) => {
           return (
               <div style = {style}>
                  <p key={index}> {text.text} </p>
                  <button style = {btnStyle}>True</button>
                  <button style = {btnFalse} onClick = {this.handleChange}>False</button>
            </div>
            )
        })

        handleChange = () => {

            this.props.score = this.props.score + 1


        }
        return (
    <div>
     {array}
    </div>
    )
 }
}

const style = {

    backgroundColor:"grey",
    marginTop: "50px"

}

const btnStyle = {
    backgroundColor: "red",
    marginRight: "10px"
}

const btnFalse = {
    backgroundColor: "green"
}

export default TriviaItem
